    public function getSourceContext()
    {
        return new Source("{% extends 'layout/base.html.twig' %}

{% block title %}Log in!{% endblock %}

{% block navbar %} {% endblock %}

{% block body %}

<div class=\"login-form\">

    <form method=\"post\">
        {% if error %}
            <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
        {% endif %}

        {% if app.user %}
            <div class=\"mb-3\">
                You are logged in as {{ app.user.userIdentifier }}, <a href=\"{{ path('app_logout') }}\">Logout</a>
            </div>
        {% endif %}

        <h1 class=\"h3 mb-3 font-weight-normal\">Please sign in</h1>
        <label for=\"inputEmail\">Email</label>
        <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" autocomplete=\"email\" required autofocus>
        <label for=\"inputPassword\">Password</label>
        <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" required>

        <input type=\"hidden\" name=\"_csrf_token\"
            value=\"{{ csrf_token('authenticate') }}\"
        >

        

            <div class=\"checkbox mb-3\">
                <label>
                    <input type=\"checkbox\" name=\"_remember_me\"> Remember me <a style=\"margin-left:30px\" href=\"{{ path('app_forgot_password_request')}}\">forgot password</a>
                </label>
            </div>
        
        <br>

        <button class=\"btn btn-lg btn-primary btn-block\" type=\"submit\">
            Sign in
        </button>
    </form>
</div>
{% endblock %}
{% block footer %} {% endblock %}
", "security/login.html.twig", "/Applications/MAMP/htdocs/projet/news-application/templates/security/login.html.twig");
